mapper.delete:
delete from flow
where flow_id = #{flowId,jdbcType=INTEGER}
  and flow_order = #{flowOrder,jdbcType=INTEGER}

mapper.update:
update flow
set role_id = #{roleId,jdbcType=INTEGER},
  description = #{description,jdbcType=VARCHAR}
where flow_id = #{flowId,jdbcType=INTEGER}
  and flow_order = #{flowOrder,jdbcType=INTEGER}

mapper.select:
select flow_id, flow_order, role_id, description
from flow
where flow_id = #{flowId,jdbcType=INTEGER}
  and flow_order = #{flowOrder,jdbcType=INTEGER}
